import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import Image from "../asset/img/wallpaper_login.png";
export default class Signup extends Component {
    constructor() {
        super();
        this.state = {
          showPopup: false
        };}
        togglePopup() {
            this.setState({
              showPopup: !this.state.showPopup
            });}
    render() {
        return (
            <>
            <div class="login">
          <div class="container">
            <div class=" row justify-content-center">
              <div
                class="card card-login border-info mb-5 mt-5"
                style={{ maxwidth: "18rem" }}
              ><img class="card-img" src={Image} alt="Card image" />
              <div class="card-img-overlay">
                <div class="card-header">
                  <p>Sign Up</p>
                </div>
                <div class="card-body text-info">
                <form class="" action="" method="post">
                          <p class="hint-text">Fill in this form to create your account!</p>
                          <div class="form-group">
                            <input type="text" class="form-control" placeholder="Username" required="required"/>
                          </div>
                          <div class="form-group">
                            <input type="password" class="form-control" placeholder="Password" required="required"/>
                          </div>
                          <div class="form-group">
                            <input type="password" class="form-control" placeholder="Confirm Password" required="required"/>
                          </div>
                          <div class="form-group">
                            <label class="form-check-label"><input type="checkbox" required="required"/> I accept the <a href="#"   onClick={this.togglePopup.bind(this)}>Terms &amp; Conditions</a></label>
                          </div>
                          <input type="submit" class="btn btn-primary btn-block" value="Sign up"/>
                        </form>
            </div>
            </div>
            </div>
            </div>
            </div>
            </div>
            {this.state.showPopup ? 
          <Popup
            text='Close Me'
            closePopup={this.togglePopup.bind(this)}
          />
          : null
        }
            </>
        )
    }
}
